// 공유저장소 : 뷰(vue) 전역(공유) 변수 정의하는 곳
import { createStore } from 'vuex'

const user = null; // TODO: 나중에 코딩할 계획(로그인할때)
export default createStore({
  state: {
    // 전역(공유) 변수 정의하는 곳
    loggedIn : user!=null? true : false // 로그인 여부
  },
})
