// FileDbDetail.vue // vueInit
<template>
  <div>
    <!-- fileTitle -->
    <div class="form-floating mb-3">
      <input
        type="text"
        class="form-control"
        id="fileTitle"
        placeholder="fileTitle"
        v-model="fileDb.fileTitle"
      />
      <label for="fileTitle">fileTitle</label>
    </div>
    <!-- fileContent -->
    <div class="form-floating mb-3">
      <input
        type="text"
        class="form-control"
        id="fileContent"
        placeholder="fileContent"
        v-model="fileDb.fileContent"
      />
      <label for="fileContent">fileContent</label>
    </div>

    <!-- TODO: 현재 이미지 보기 -->
    <div class="mb-3 col-12">
      <img :src="fileDb.fileUrl" width="200px" alt="이미지" />
    </div>

    <!-- 파일선택상자 -->
    <div class="input-group">
      <input type="file" class="form-control" ref="file" @change="select" />
      <button class="btn btn-warning" type="button" @click="update">
        수정
      </button>
    </div>
    <div class="mt-3">
      <button class="btn btn-danger" type="button" @click="remove">삭제</button>
    </div>
  </div>
</template>

<script>
import FileDbService from "@/services/advanced/FileDbService";
export default {
  data() {
    return {
      fileDb: {
        uuid: this.$route.params.uuid, // 기본키
        fileTitle: "",
        fileContent: "",
        fileUrl: "", // 다운로드 url
        image: undefined, // 선택이미지
      },
    };
  },
  methods: {
    async getDetail(uuid) {
      try {
        let response = await FileDbService.get(uuid);
        console.log(response.data); // 디버깅
        this.fileDb = response.data;
      } catch (error) {
        console.log(error);
      }
    },
    select() {
      this.fileDb.image = this.$refs.file.files[0];
    },
    async update() {
      try {
        // TODO: fileDB -> fileDb 고칠것
        let response = await FileDbService.update(
          this.fileDb.uuid,
          this.fileDb
        );
        console.log(response.data); // 디버깅
        alert("수정되었습니다.")
      } catch (error) {
        this.fileDb.image = undefined;
        console.log(error);
      }
    },
    async remove() {
      try {
        let response = await FileDbService.remove(this.fileDb.uuid);
        console.log(response.data); // 디버깅
        // 전체조회 화면 강제 이동
        this.$router.push("/fileDb");
      } catch (error) {
        console.log(error);
      }
    },
  },
  mounted() {
    this.getDetail(this.$route.params.uuid);
  },
};
</script>
