// services - EmpService.js
import axios from "axios";
const baseURL = "http://localhost:8000/api"

// TODO: 웹토큰 가져오기(로컬스토리지) , user.accessToken
let user = JSON.parse(localStorage.getItem("user"));
// TODO: 벡엔드 전송(웹토큰)
// 예 : headers : { Authorization: "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIi..." }
const token = { Authorization: "Bearer "+ user?.accessToken };
// TODO: 전송 : axios.get("url", {headers: token}), axios.post("url", data, {headers: token})

// 전체조회
const getAll = (searchKeyword, pageIndex, recordCountPerPage) => {
    return axios
       .get(baseURL+`/basic/emp?searchKeyword=${searchKeyword}&pageIndex=${pageIndex}&recordCountPerPage=${recordCountPerPage}`, {headers: token});
};
// TODO: 사원생성 : url(/basic/emp)
const insert = (data) => {
    return axios.post(baseURL+"/basic/emp", data, {headers: token});
}
// TODO: 상세조회 : url(/basic/emp/{eno})
const get = (eno) => {
    return axios.get(baseURL+`/basic/emp/${eno}`, {headers: token});
}
// TODO: 수정(dno, data) : 벡엔드 수정 url : /basic/emp/{eno}
const update = (eno, data)=>{
    return axios.put(baseURL+`/basic/emp/${eno}`,data, {headers: token});
}
// TODO: 삭제(eno) : 벡엔드 삭제 url: /basic/emp/deletion/{eno}
const remove = (eno)=>{
    return axios.delete(baseURL+`/basic/emp/deletion/${eno}`, {headers: token});
}
// 객체 : getAll 넣어 export
const EmpService = {
    getAll,
    insert,
    get,
    update,
    remove
}
export default EmpService;
