import { createApp } from "vue";
import App from "./App.vue";

import router from "./router";
import store from "./store";

// 아이콘 및 기본 스타일 로딩
import "bootstrap-icons/font/bootstrap-icons.css";

// Bootstrap 스타일 및 스크립트 로딩
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js"; // bootstrap.bundle.min.js로 수정, 이 파일에 필요한 모든 스크립트가 포함됨

// BootstrapVue3 로딩
import { BootstrapVue3 } from "bootstrap-vue-3";
import "bootstrap-vue-3/dist/bootstrap-vue-3.css"; // 이 부분은 필요

// Vue 앱 설정 및 `bootstrap-vue-3` 연결
createApp(App)
  .use(BootstrapVue3) // BootstrapVue3 플러그인 등록
  .use(store)
  .use(router)
  .mount("#app");
