// LoginView.vue // vueInit
<template>
  <div>
    <div class="row justify-content-center">
      <div class="col-xl-10 col-lg-12 col-md-9">
        <div class="card mt-5">
          <div class="card-body p-0">
            <!-- {/* Nested Row within Card Body */} -->
            <div class="row">
              <!-- TODO: 이미지(왼쪽:강아지+사람) -->
              <div class="col-lg-6">
                <img
                  :src="require('@/assets/images/puppy-1920_1280_2.jpg')"
                  class="img-fluid"
                />
              </div>
              <!-- TODO: 입력양식(오른쪽) -->
              <div class="col-lg-6">
                <div class="p-5">
                  <div class="text-center">
                    <h1 class="h4 mb-4">Welcome Back!</h1>
                  </div>
                  <div class="user">
                    <div class="form-group">
                      <input
                        type="text"
                        class="form-control form-control-user mb-3"
                        placeholder="이메일을 넣기"
                        v-model="user.email"
                      />
                    </div>
                    <div class="form-group">
                      <input
                        type="password"
                        class="form-control form-control-user mb-3"
                        placeholder="패스워드 넣기"
                        v-model="user.password"
                      />
                    </div>

                    <button
                      class="btn btn-primary btn-user w-100 mb-3"
                      @click="login"
                    >
                      Login
                    </button>
                  </div>
                  <hr />
                  <div class="text-center">
                    <a class="small" href="/register"> Create an Account! </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import MemberService from "@/services/auth/MemberService";
export default {
  data() {
    return {
      user: {
        email: "",
        password: "",
      },
    };
  },
  methods: {
    // 샘플 => 수정 : 추천(방법)
    async login() {
      try {
        let response = await MemberService.login(this.user);
        console.log(response.data); // 디버깅
        // TODO: 공유변수 : loggedIn 상태 변경 : true
        // TODO: vue/react 공유저장소 단점 : 새로고침(정보 사라짐)
        // TODO: 보완 : 쿠키/로컬스토리지 등 이용 : 값을 저장
        //       (웹브라우저 기본 기능)
        this.$store.state.loggedIn = true;
        // TODO: 사용법(저장) : localStorage.setItem("키", 값(문자열));
        // js : 객체 -> 문자열 변환 : JSON.stringify(객체)
        localStorage.setItem("user", JSON.stringify(response.data));
        // 홈으로 강제이동 : /
        this.$router.push("/");
      } catch (error) {
        console.log(error);
      }
    },
  },
  // TODO: 화면이 뜰 때 이미 회원이면 홈(/)으로 강제이동
  mounted() {
    // TODO: vue 의 전역변수(공유변수) 관리하는 곳
    if (this.$store.state.loggedIn == true) {
      this.$router.push("/");
    }
  },
};
</script>
